mapper.select:
SELECT 
	
FROM tjrb_invest_product_insure a

WHERE a.id = #{id}

mapper.update:
UPDATE tjrb_invest_product_insure SET 	
	ad_pic_url = #{adPicUrl},
	product_id = #{productId},
	ad_position = #{adPosition},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE tjrb_invest_product_insure SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
